function openTag(t, attrs, selfClosing) {
    attrs = attrs || [];

    // Open
    var tag = '<'+t;

    // Add attributes
    attrs.forEach(function(attr) {
        tag += ' '+attr.name+'="'+attr.value+'"';
    });
    // Close
    if (selfClosing) {
        tag += '/';
    }
    tag += '>';

    return tag;
}

function closeTag(t) {
    return '</'+t+'>';
}

module.exports = {
    openTag: openTag,
    closeTag: closeTag
};